mapper.select:
SELECT restriction_id, patient_id, restriction_type, active_yn, reason, start_at, end_at, created_by FROM patient_restriction
TO_CHAR(patient_id) LIKE '%' || #{keyword} || '%'
restriction_type = #{keyword}
active_yn = #{keyword}
reason LIKE '%' || #{keyword} || '%'
ORDER BY start_at DESC, restriction_id DESC
